const norm = s => {
  try{
    if(!s || typeof s !== 'string') return null;
    const cleaned = String(s).replace(/[^0-9.]/g, '');
    if(!cleaned) return null;
    return Number(cleaned.replace(/,/g,''));
  }catch(e){ return null; }
};
const results = [];
for(const i of items){
  try{
    if(typeof i.json === 'string'){
      const text = i.json;
      const re = /‚Çπ\s*([0-9,]+)/g;
      const matches = Array.from(text.matchAll(re)).slice(0,5);
      if(matches.length){
        for(const m of matches){
          const p = norm(m[1]);
          if(p) results.push({ json: { vendor: 'Amazon/Other', price: p, mrp: null, url: null, timestamp: new Date().toISOString() } });
        }
      } else {
        results.push({ json: { vendor: 'Amazon/Other', raw: text.slice(0,200) } });
      }
    } else if(Array.isArray(i.json)){
      for(const row of i.json){
        try{
          if(row && row.price){
            results.push({ json: { vendor: row.vendor||'Flipkart', title: row.title||null, price: norm(row.price), mrp: norm(row.mrp), url: row.url||null, timestamp: row.timestamp||new Date().toISOString() } });
          } else if(row && row.error){
            results.push({ json: { vendor: 'Flipkart', error: row } });
          }
        }catch(e){ results.push({ json: { vendor: 'Flipkart', error: e.message } }); }
      }
    } else {
      results.push({ json: i.json });
    }
  }catch(e){ results.push({ json: { error: 'normalize_failed', message: e && e.message } }); }
}
return results;

return items.map(it => {
  try{
    const db = it.json['Baseline Lookup'];
    const baseline = db && db[0] && db[0].median_price ? Number(db[0].median_price) : (it.json.mrp || it.json.price || null);
    return { json: { ...it.json, baseline } };
  }catch(e){
    return { json: { ...it.json, baseline: it.json.mrp || it.json.price || null, baseline_error: e.message } };
  }
});

return items.map(it => {
  try{
    const score = (it.json && it.json.score) || (it.json && it.json.result && it.json.result.score) || null;
    return { json: { ...it.json, ml_score: score === null ? 0.5 : Number(score) } };
  }catch(e){
    return { json: { ...it.json, ml_score: 0.5, ml_error: e.message } };
  }
});

return items.map(it => {
  try{
    const price = Number(it.json.price || 0);
    const baseline = Number(it.json.baseline || price || 0);
    const discount = baseline ? (1 - (price / baseline)) : 0;
    const ml = Number(it.json.ml_score || 0.5);
    const flag = (discount >= 0.95) && (ml >= 0.85);
    return { json: { ...it.json, baseline, discount, ml, flag } };
  }catch(e){ return { json: { ...it.json, flag: false, decision_error: e.message } }; }
});

return items.map(i => {
  try{
    const err = i.json.error || i.json.error_message || i.json.normalize_error || null;
    const nodeName = i.json.__nodeName || i.json.node || 'unknown';
    const payload = { node: nodeName, error: err, data: i.json };
    return { json: payload };
  }catch(e){ return { json: { node: 'Format Error Report', error: e.message } }; }
});
